mapper.select:
select ca01_id, ca01_nome, ca01_apelido, ca01_sexo
from ca01_integrante
where ca01_id = #{id}

mapper.update:
update ca01_integrante
set ca01_nome    = #{integrante.nome},
    ca01_apelido = #{integrante.apelido},
    ca01_sexo    = #{integrante.sexo}
where ca01_id = #{integrante.id}
