mapper.select:
select id, nome, data_criacao, descricao
from podcast
where id = #{id}

select id, nome, data_criacao, descricao
from podcast
where nome = #{nome}

mapper.update:
update podcast
set nome         = #{podcast.nome},
    data_criacao = #{podcast.dataCriacao},
    descricao    = #{podcast.descricao}
where id = #{podcast.id}
